import { normaliseDegree } from "../../utils/math";
import { convertToZodiacString } from "../utils";

export const calculateEqualHouses = ({ ascendant }) => {
  const cusps = [];
  for (let i = 0; i < 12; i++) {
    const degree = normaliseDegree(ascendant + i * 30);
    cusps.push({
      degree,
      zodiacDegree: convertToZodiacString(degree),
    });
  }
  return cusps;
};
