import { calculateCampanusHouses } from "./campanus";
import { calculateEqualHouses } from "./equal";
import { calculateKochHouses } from "./koch";
import { calculatePorphyryHouses } from "./porphyry";
import { calculateRegiomontanusHouses } from "./regiomontanus";
import { calculateTopocentricHouses } from "./topocentric";

export const calculateHouses = ({
  angles,
  latitude,
  obliquity,
  siderealTimeAngle,
}) => {
  const data = {
    ascendant: angles.ascendant.degree,
    descendant: angles.descendant.degree,
    midheaven: angles.midheaven.degree,
    lowerMidheaven: angles.lowerMidheaven.degree,
    latitude,
    obliquity,
    siderealTimeAngle,
  };
  return {
    // campanus: calculateCampanusHouses(data),
    equal: calculateEqualHouses(data),
    koch: calculateKochHouses(data),
    porphyry: calculatePorphyryHouses(data),
    regiomontanus: calculateRegiomontanusHouses(data),
    topocentric: calculateTopocentricHouses(data),
  };
};
