export const degreesToRadians = ($num) => ($num * Math.PI) / 180;
export const radiansToDegrees = ($num) => ($num * 180) / Math.PI;
export const normaliseDegree = ($degree) => {
  // cos Javascript can't handle floating point mathematics
  const stringDegree = ($degree + 360).toString();
  const splitString = stringDegree.split(".");
  const degree = Number(splitString[0]) % 360;
  const decimals = splitString[1] || "0";
  return Number(`${degree}.${decimals}`);
};
